        merge_object : function(obj,obj1){
            var objVal;
            var obj1Val;
            for(var key in obj1) {
                objVal = obj[key];
                obj1Val = obj1[key];
                let arrayType = Array.isArray(objVal);
                if(objVal && typeof objVal == 'object' && !arrayType) {
                    this.merge_object(objVal,obj1Val);
                } else {
                    if(arrayType) {
                        obj1Val.push.apply(obj1Val,objVal)
                    }
                    obj[key] = obj1Val;
                }
            }
        },

