import { prop } from "@slyte/core";
import { Component } from "../component.js";

class LyteSvgComponent extends Component {
    constructor() {
        super();
    }

    data() {
		return {
			ltPropViewBox: prop( 'string', { 'default': '' } ),
			ltPropClass: prop( 'string', { 'default': '' } ),
			ltPropPath: prop( 'string' ),
			ltPropId: prop( 'string', { 'default': '' } ),
			flag: prop( 'boolean', { default: false } )
		};		
	}

    didConnect() {
		this.setData( 'flag', true );
	}
}

export { LyteSvgComponent };